INSERT INTO `tramites` (`idtramite`, `idsecretaria`, `idarea`, `nombre`, `requisitos`, `costo`, `portal`, `formato`, `idtipo_tramite`, `id_permiso`, `telefono_pcorp`, `direccion_pcorp`, `cita_web`) VALUES
(1, 4, 25, 'Apoyo a Personas Vulnerables', '<ol>\r\n	<li>Original y copia de INE.</li>\r\n	<li>Copia de la curp.</li>\r\n	<li>Copia de comprobante de domicilio.</li>\r\n	<li>Además es necesario presentarse en las oficinas de Gestión Social.</li>\r\n</ol>\r\n', 'Sin costo', NULL, '/archivos/documentos/Secretaria%20Particular/18/02/APOYO%20A%20PERSONAS%20VULNERABLES.pdf', NULL, '25233714715820063', '4499650088 Ext. 1072 y 1081', 'Calle Emiliano Zapata # 109, Zona Centro, Jesús María, Ags.', NULL),
(2, 6, 40, 'Alta al Padrón de Proveedores', '<ol>\r\n<li>Llenar solicitud de inscripción al Padrón Municipal de Proveedores.</li>\r\n<li>Solicitud de inscripción al Registro de Contribuyentes (r1).</li>\r\n<li>Cedula fiscal actualizada.</li>\r\n<li>Relación de productos y servicios (catalogo y folletos, solo fabricantes).</li>\r\n<li>Copia de factura cancelada.</li>\r\n<li>Curriculum empresarial.</li>\r\n<li>Acta constitutiva (en caso de sociedades).</li>\r\n<li>Poder notarial de representante legal (si firma solicitud de inscripción).</li>\r\n<li>Copia de identificación.</li>\r\n</ol>\r\n', 'Trámite Gratuito', NULL, '/archivos/documentos/Secretar%C3%ADa%20de%20Finanzas/19/01/ALTA%20AL%20PADRON%20DE%20PROVEEDORES.pdf', NULL, '25233714715820065', '4499650088 ext. 1048, 1027', 'Emiliano Zapata #109, Centro, Jesús María, Aguascalientes', NULL),
(3, 6, 40, 'Impuesto sobre Adquisición de Bienes Inmuebles (I.S.A.B.I.)', '<ol>\r\n<li>Formato de traslado de dominio ante un notario.</li>\r\n<li>Predial Pagado.</li>\r\n<li>Avaluo catastral y comercial.</li>\r\n<li>Presentar el segundo aviso preventivo, emitido por el registro p&uacute;blico de la propiedad</li>\r\n</ol>\r\n', 'Según Corresponda', NULL, '/archivos/documentos/Secretar%C3%ADa%20de%20Finanzas/19/01/isabi.pdf', NULL, '25233714715820065', '4499650088 ext. 1083, 1027', 'Emiliano Zapata #109, Centro, Jesús María, Aguascalientes', NULL),
(4, 6, 40, 'Impuesto Predial', '<ol>\r\n<li>No. de cuenta catastral, nombre del propietario o ubicación del inmueble.</li>\r\n<li>Copia del pago anterior.</li>\r\n<li>Chequeo de bases de datos.</li>\r\n</ol>\r\n', 'Según Corresponda', 'http://jesusmaria.gob.mx/#/predial', '/archivos/documentos/Secretar%C3%ADa%20de%20Finanzas/19/01/prediial.pdf', NULL, '25233714715820065', '4499650088  ext. 1083, 1027', 'Emiliano Zapata #109, Centro, Jesús María, Aguascalientes', 1),
(5, 6, 40, 'Revalidación de Licencia de Funcionamiento Reglamentada', '<ol>\r\n<li>Pago total según corresponda en el artículo 27 de la Ley de Ingresos Municipal.</li>\r\n<li>Nombre del titular o negocio.</li>\r\n</ol>\r\n', 'Según Corresponda', NULL, '/archivos/documentos/Secretar%C3%ADa%20de%20Finanzas/19/01/PAGO%20POR%20REVALIDACION%20DE%20LICENCIA.pdf\r\n', NULL, '25233714715820065', '4499650088  ext. 1083, 1027', 'Emiliano Zapata #109, Centro, Jesús María, Aguascalientes', NULL),
(6, 6, 40, 'Reembolso (Reintegro de pagos por error de cobro)', '<ol>\r\n<li>Copia de recibos originales.</li>\r\n<li>Identificación oficial.</p>\r\n</ol>\r\n', 'Trámite Gratuito', NULL, '/archivos/documentos/Secretar%C3%ADa%20de%20Finanzas/19/01/reembolso.pdf', NULL, '25233714715820065', '4499650088 ext. 1083, 1027', 'Emiliano Zapata #109, Centro, Jesús María, Aguascalientes', NULL),
