(114, 1, 11, 'Préstamo y Renta de Espacios Deportivos', '<ol>\r\n<li>Instituci&oacute;n o club del que proviene.</li>\r\n<li>Raz&oacute;n por la cual solicita el apoyo.</li>\r\n<li>Horarios y las fechas sugeridas.</li>\r\n<li>Cubrir con el dep&oacute;sito en garant&iacute;a.</li>\r\n</ol>\r\n<p style=\"text-align: justify;\">Auditorio deportivo (por hora) $148.00<br />Parque de B&eacute;isbol para evento deportivo (por juego) $1431.00<br />Unidad deportiva \"Cielo claro\" cancha de voleibol/basquetbol (por juego) $47.00<br />Unidad deportiva \"Cielo claro\" canchas de squash (por hora) $70.00<br />Unidad deportiva \"Luis Donaldo Colosio\" cancha empastada F&uacute;tbol (por juego )$477.00<br />Unidad deportiva \"Valle de Margaritas\" cancha de Futbol R&aacute;pido (por juego) $82.00<br />Uso de canchas deportivas sin pasto (por juego) $84.00<br />Skatorama \"Jard&iacute;n del deporte\" (por hora) $110.00<br />Skatorama \"Manantiales del Pinar\"(por dia) $38.00</p>', 'Variable', NULL, '/archivos/documentos/Presidencia%20Municipal/19/01/prestamo%20y%20renta%20de%20espacios%20deportivos.pdf', NULL, '25415551618648811', '4493616051', 'Matamoros #101 Planta Alta', NULL),
(115, 2, 17, 'Liberación de Vehículos', '<ol>\n	<li>Liquidar los daños ocasionados a municipio en caso de que haya.</li>\n	<li>Liquidar multas de tránsito.</li>\n	<li>Acreditar la propiedad.</li>\n</ol>\n', 'Según sea el daño', NULL, '/archivos/documentos/Presidencia%20Municipal/18/02/liberacion%20de%20vehiculos%20presidencia.pdf', NULL, '25415551618648811', '4499650088 EXT. 1014', 'Emiliano Zapata no. 109 Col. Centro, Jesús María, Ags., C.P.20920', NULL),
(116, 2, 17, 'Mediación', '<ol>\n	<li>Exponer problemática.</li>\n	<li>Datos generales de la persona que solicita la mediación.</li>\n	<li>Datos generales de la persona que se pretende citar.</li>\n</ol>\n', 'Gratuito', NULL, '/archivos/documentos/Presidencia%20Municipal/18/02/MEDIACION%20presidencia.pdf', NULL, '25415551618648811', '4499650088 EXT. 1014', 'Emiliano Zapata No. 109 Col. Centro, Jesús María Ags. C.P. 20920', NULL),
