CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `view_secretarias`  AS  select `s`.`idsecretaria` AS `idsecretaria`,`a`.`idarea` AS `idarea`,`s`.`secretaria` AS `secretaria`,`d`.`denominaciondelcargonombramientootorgado` AS `denominaciondelcargonombramientootorgado`,`d`.`tipodevialidad` AS `tipodevialidad`,`d`.`nombredevialidad` AS `nombredevialidad`,`d`.`numeroexterior` AS `numeroexterior`,`d`.`numerointerior` AS `numerointerior`,`d`.`tipodeasentamiento` AS `tipodeasentamiento`,`d`.`nombredelasentamiento` AS `nombredelasentamiento`,`d`.`codigopostal` AS `codigopostal`,`d`.`numerosdetelefonooficialyextension` AS `numerosdetelefonooficialyextension`,`d`.`extension` AS `extension`,`a`.`mapa_google` AS `mapa_google`,`a`.`url_mapa` AS `url_mapa`,`s`.`externa` AS `externa`,count(`t`.`idsecretaria`) AS `num_tramites` from (((`areas` `a` left join `secretarias` `s` on((`s`.`idsecretaria` = `a`.`idsecretaria`))) left join `dir_transparencia` `d` on((if((`a`.`area` = 'Misma Secretaria'),`s`.`secretaria`,`a`.`area`) = `d`.`denominaciondelcargonombramientootorgado`))) left join `tramites` `t` on(((`s`.`idsecretaria` = `t`.`idsecretaria`) and (`s`.`idsecretaria` = `d`.`idsecretaria`) and (`s`.`idsecretaria` = `t`.`idsecretaria`)))) where (`a`.`area` = 'Misma Secretaria') group by `s`.`idsecretaria` order by `s`.`idsecretaria`,`a`.`idarea` ;

