DROP TABLE IF EXISTS `view_tramites_cantidad_area`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `view_tramites_cantidad_area`  AS  select `s`.`idsecretaria` AS `idsecretaria`,`s`.`secretaria` AS `secretaria`,`a`.`idarea` AS `idarea`,`a`.`area` AS `area`,if((`a`.`area` = 'Misma Secretaria'),`s`.`secretaria`,`a`.`area`) AS `area_correcta`,count(`t`.`idtramite`) AS `cantidad` from ((`secretarias` `s` join `areas` `a`) join `tramites` `t`) where ((`t`.`idsecretaria` = `s`.`idsecretaria`) and (`s`.`idsecretaria` = `a`.`idsecretaria`) and (`t`.`idarea` = `a`.`idarea`)) group by `a`.`idarea` order by `s`.`idsecretaria`,`a`.`idarea` ;

