CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `view_tramites_con_cita`  AS  select `t`.`idtramite` AS `idtramite`,`s`.`secretaria` AS `secretaria`,`a`.`idsecretaria` AS `idsecretaria`,`a`.`denominaciondelcargonombramientootorgado` AS `denominaciondelcargonombramientootorgado`,`t`.`nombre` AS `nombre`,`t`.`requisitos` AS `requisitos`,`t`.`costo` AS `costo`,concat(`a`.`tipodevialidad`,' ',`a`.`nombredevialidad`,' no. ',`a`.`numeroexterior`,`a`.`numerointerior`,' ',`a`.`tipodeasentamiento`,' ',`a`.`nombredelasentamiento`) AS `domicilio`,`a`.`numerosdetelefonooficialyextension` AS `numerosdetelefonooficialyextension` from ((`tramites` `t` join `secretarias` `s`) join `view_areas_direcciones` `a`) where ((`t`.`idsecretaria` = `s`.`idsecretaria`) and (`t`.`idarea` = `a`.`idarea`) and (`t`.`cita_web` = 1)) order by `t`.`nombre` ;

